(function(){
	//All globally needed Angular JS Modules shall be inserted here:
	angular.module('bgts',['defaultnavigator'])
	.filter('unsafe', function($sce) { return $sce.trustAsHtml; })
	.run(function(){
		console.log("BGTS Laravel-Angular app - Started.");
	})
	.directive('defaultNavbar',[function(){
		return {
			restrict: 'E',
			templateUrl: "navbar"
		};
	}])
	.directive('defaultSidebar',[function(){
		return {
			restrict: 'E',
			templateUrl: "sidebar"
		};
	}])
	.directive('defaultFooter',[function(){
		return {
			restrict: 'E',
			templateUrl: "footer"
		};
	}])
	.controller('IndexController',[function(){
		// alert("public/Modules/default/js/default-main.min.js says --- Angular JS works!");
	}])
})();
